svg.script:
var motion_picture = document.getElementById("motion_picture");
console.log(motion_picture);

motion_picture.addEventListener("click",function(){
  console.log("clicked");

  if(motion_picture.classList[0]==="still") {
     motion_picture.setAttribute('class','motion'); 
     motion_picture.setAttribute('xlink:href', 'put_your_work_in_a_safe_place.gif');
  } 
  else {
     motion_picture.setAttribute('class','still'); 
     motion_picture.setAttribute('xlink:href', 'put_your_work_in_a_safe_place.svg');
  } 

});
